import React,{Component} from 'react';
import axios from 'axios';

class BookAppoinment extends Component{
    state={
        doctors:[],
        appoinment:{ doctorname:'',ptname:'',ptage:'',ptphnno:'',ptemail:'',ptappdate:'',ptstbltime:'' }
    }

    componentDidMount(){
        axios.get('http://127.0.0.1:8000/doctor/doctorlist/').then(response => this.setState({ doctors:response.data }));
    }

    inputchanged= event =>{
        const cred = this.state.appoinment;
        cred[event.target.name]= event.target.value;
        this.setState({appoinment: cred});
    }
    appoinmentpost= e =>{
        e.preventDefault();
        axios.post('http://127.0.0.1:8000/appoinment/appoinment/', this.state.appoinment).then((response) => {
            console.log(response);
        }, (error) => {
            console.log(error);
        });
  };

    render(){
        return(
            <div classNameName="hompagebody">
                <div className="row justify-content-end">
                    <div className="col-lg-7">
                        <div className="popup_box ">
                            <div className="popup_inner">
                                <h3>
                                    Book an 
                                <span>Appointment</span>
                                </h3>
                                <form action="#">
                                    <div className="row">
                                        <div className="col-xl-12">
                                            <select className="form-select wide" id="default-select" className="">
                                                <option data-display="Please select doctor to visit">Please select doctor to visit </option>
                                                {
                                                    this.state.doctors.map(doctor =>(
                                                    <option value={doctor.fullname} onChange={this.inputchanged}>{doctor.fullname} ({doctor.achievement})</option>
                                                    ))
                                                }

                                            </select>
                                        </div>
                                        <div className="col-xl-9">
                                            <input type="text" value={this.state.appoinment.ptname} onChange={this.inputchanged} placeholder="Your name " required/>
                                        </div>
                                        <div className="col-xl-3">
                                            <input type="text" value={this.state.appoinment.ptage} onChange={this.inputchanged} placeholder="Your age" required/>
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="text" value={this.state.appoinment.ptphnno} onChange={this.inputchanged} placeholder="Phone number " required/>
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="email" value={this.state.appoinment.ptemail} onChange={this.inputchanged} placeholder="Email Address" required/>
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="datepicker" value={this.state.appoinment.ptappdate} onChange={this.inputchanged} placeholder="Appointment Date" required/>
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="timepicker" value={this.state.appoinment.ptstbltime} onChange={this.inputchanged} placeholder="Suitable time" required/>
                                        </div>
                                        <div className="col-xl-12">
                                            <button type="submit" onClick={this.appoinmentpost} className="boxed-btn3">Make an Appointment</button>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default BookAppoinment;
