import React,{Component} from 'react';
import axios from 'axios';

class BookAppoinment extends Component{
    state={
        appoinment:{ doctorname:'Mahidul Moon',ptname:'',ptage:'',ptphnno:'',ptemail:'',ptappdate:'',ptstbltime:'' },
        doctors:[]

    }

    componentDidMount(){
        axios.get('http://127.0.0.1:8000/doctor/doctorlist/').then(response => this.setState({ doctors:response.data })).catch(error => {
            console.log(error);            // react on errors.
          });
    }


    inputchanged2= event =>{


        this.setState.appoinment({ ptname: event.target.value });

    }
    inputchanged3= event =>{


        this.setState({ ptage: event.target.value });

    }
    inputchanged4= event =>{


        this.setState({ ptphnno: event.target.value });

    }
    inputchanged5= event =>{


        this.setState({ ptemail: event.target.value });

    }
    inputchanged6= event =>{


        this.setState({ ptappdate: event.target.value });

    }
    inputchanged7= event =>{


        this.setState({ ptstbltime: event.target.value });

    }
    appoinmentpost= e =>{
        console.log(this.state.appoinment);

        //axios.post('http://127.0.0.1:8000/appoinment/appoinment/', this.state.appoinment).then((response) => {
          //  console.log(response);
        //}, (error) => {
          //  console.error(error);
        //});
        e.preventDefault();
  };

    render(){
        return(
            <div classNameName="hompagebody">
                <div className="row justify-content-end">
                    <div className="col-lg-7">
                        <div className="popup_box ">
                            <div className="popup_inner">
                                <h3>
                                    Book an 
                                <span>Appointment</span>
                                </h3>
                                <form>
                                    <div className="row">
                                        <div className="col-xl-12">
                                            <select className="form-select wide" id="default-select" className="">
                                                <option data-display="Please select doctor to visit">Please select doctor to visit </option>
                                                {
                                                    this.state.doctors.map(doctor =>(
                                                    <option value="Mahidul Moon" onChange={this.inputchanged1}  >{doctor.fullname} ({doctor.achievement})</option>
                                                    ))
                                                }

                                            </select>
                                        </div>
                                        <div className="col-xl-9">
                                            <input type="text" value={this.state.ptname} onChange={this.inputchanged2} placeholder="Your name " />
                                        </div>
                                        <div className="col-xl-3">
                                            <input type="text" value={this.state.ptage} onChange={this.inputchanged3} placeholder="Your age" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="text" value={this.state.ptphnno} onChange={this.inputchanged4} placeholder="Phone number " />
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="email" value={this.state.ptemail} onChange={this.inputchanged5} placeholder="Email Address" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="datepicker" value={this.state.ptappdate} onChange={this.inputchanged6} placeholder="Appointment Date" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="timepicker" value={this.state.ptstbltime} onChange={this.inputchanged7} placeholder="Suitable time" />
                                        </div>
                                        <div className="col-xl-12">
                                            <input type="submit" name="" className="boxed-btn3" onClick={this.appoinmentpost} />
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default BookAppoinment;
