import React,{Component} from 'react';

class Registration extends Component{
    state={
        credentials:{username:'',password:'',email:'',first_name:'',last_name:''}
    }
    inputchanged= event =>{
        const cred = this.state.credentials;
        cred[event.target.name]= event.target.value;
        this.setState({credentials: cred});
    }
    register = event =>{
        fetch('http://127.0.0.1:8000/doctor/doctorregister/',{
            method:'POST',
            headers:{'Content-Type': 'application/json'},
            body: JSON.stringify(this.state.credentials)
        }).then( data => data.json()
        ).catch(error=>console.error(error))

    }

    render(){
        return(
            <div className="hompagebody">
                <div className="page-content">
                    <div className="form-v4-content">
                        <div className="form-left">
                            <h2>INFOMATION</h2>
                            <p className="text-1">1.Unique email id 2.Same Password for both password field 3.Unique username.</p>
                            <p className="text-2"><span>Note:</span> If you already have an account than click the button bellow and Login.</p>
                            <div className="form-left-last">
                                <a href="/login">Have An Account???</a>
                            </div>
                        </div>
                        <form className="form-detail"  id="myform">
                            <h2>REGISTER FORM</h2>
                            <div className="form-row">
                                <label>Username</label>
                                <input type="text" name="username" id="username" value={this.state.credentials.username} onChange={this.inputchanged} className="input-text" required />
                            </div>
                            <div className="form-group">
                                <div className="form-row form-row-1 ">
                                    <label for="password">Password</label>
                                    <input type="password" name="password" id="password" value={this.state.credentials.password} onChange={this.inputchanged} className="input-text" required />
                                </div>

                            </div>
                            <div className="form-row">
                                <label>Your Email</label>
                                <input type="text" name="email" id="email" value={this.state.credentials.email} onChange={this.inputchanged} className="input-text" required />
                            </div>
                            <div className="form-group">				
                                <div className="form-row form-row-1">
                                    <label >First Name</label>
                                    <input type="text" name="first_name" id="first_name" value={this.state.credentials.first_name} onChange={this.inputchanged} className="input-text"  />
                                </div>
                                <div className="form-row form-row-1">
                                    <label >Last Name</label>
                                    <input type="text" name="last_name" id="last_name" value={this.state.credentials.last_name} onChange={this.inputchanged} className="input-text" required />
                                </div>
                            </div>


                            <div className="form-row-last">
                                <input type="submit" name="" className="register" onClick={this.register} />
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Registration;
