import React,{Component} from 'react';
import axios from 'axios';

class BookAppoinment extends Component{
    state={
        doctors:[],
        appoinment:[]
    }

    componentDidMount(){
        axios.get('http://127.0.0.1:8000/doctor/doctorlist/').then(response => this.setState({ doctors:response.data }));
    }

    inputchanged= event =>{
        const cred = this.state.appoinment;
        cred[event.target.name]= event.target.value;
        this.setState({appoinment: cred});
    }

    render(){
        return(
            <div classNameName="hompagebody">
                <div className="row justify-content-end">
                    <div className="col-lg-7">
                        <div className="popup_box ">
                            <div className="popup_inner">
                                <h3>
                                    Book an 
                                <span>Appointment</span>
                                </h3>
                                <form action="#">
                                    <div className="row">
                                        <div className="col-xl-12">
                                            <select className="form-select wide" id="default-select" className="">
                                                <option data-display="Please select doctor to visit">Please select doctor to visit </option>
                                                {
                                                    this.state.doctors.map(doctor =>(
                                                    <option value="1">{doctor.fullname} ({doctor.achievement})</option>
                                                    ))
                                                }

                                            </select>
                                        </div>
                                        <div className="col-xl-9">
                                            <input type="text"  placeholder="Your name " />
                                        </div>
                                        <div className="col-xl-3">
                                            <input type="text"  placeholder="Your age" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="text"  placeholder="Phone number " />
                                        </div>
                                        <div className="col-xl-6">
                                            <input type="email"  placeholder="Email Address" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="datepicker" placeholder="Appointment Date" />
                                        </div>
                                        <div className="col-xl-6">
                                            <input className="timepicker" placeholder="Suitable time" />
                                        </div>
                                        <div className="col-xl-12">
                                            <button type="submit" className="boxed-btn3">Make an Appointment</button>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default BookAppoinment;
